class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        @lru_cache(None)
        def backtrack(i, prev1, prev2):
            if i == n:
                return 1

            count = 0
            for x in range(prev1, nums[i] + 1):
                y = nums[i] - x
                if y <= prev2:
                    count = (count + backtrack(i + 1, x, y)) % MOD

            return count

        return backtrack(0, 0, nums[0])
