public class Solution {
    private static final int MOD = 1_000_000_007;
    private int[][][] memo;

    public Solution() {
        memo = new int[0][][];
    }

    private int calculatePairs(int[] nums, int index, int limit1, int limit2) {
        if (index >= nums.length) {
            return 1;
        }

        if (memo[index][limit1][limit2] != -1) {
            return memo[index][limit1][limit2];
        }

        int result = 0;
        for (int x = 0; x <= nums[index]; x++) {
            int value1 = x;
            int value2 = nums[index] - x;
            if (value1 >= limit1 && value2 <= limit2) {
                result = (result + calculatePairs(nums, index + 1, value1, value2)) % MOD;
            }
        }

        memo[index][limit1][limit2] = result;
        return result;
    }

    public int countOfPairs(int[] nums) {
        int length = nums.length;
        memo = new int[length][51][51]; // nums[i] <= 50
        for (int i = 0; i < length; i++) {
            for (int j = 0; j <= 50; j++) {
                for (int k = 0; k <= 50; k++) {
                    memo[i][j][k] = -1;
                }
            }
        }
        return calculatePairs(nums, 0, 0, 50);
    }
}
