class Solution {
    private:
    int rec(int i , int j ,vector<vector<int>>&dp , vector<int>&v , int n){
        int mod=1e9+7;
        if(i>=n){
            return 1;
        }
        if(dp[i][j]==-1){
            int cnt=0;
            int x;
            x=v[i]-v[i-1]+j;
            x=max(x,j);
            for(int j=x ; j<=v[i] ; j++){
                int vl=rec(i+1,j,dp,v,n);
                cnt=((cnt%mod)+(vl%mod))%mod;
            }
            return dp[i][j]=cnt;
        }
        else return dp[i][j];
    }
public:
    int countOfPairs(vector<int>&v) {
        int mod=1e9+7;
        int n=v.size();
        vector<vector<int>>dp;
        for(int i=0 ; i<n ; i++){
            vector<int>tmp(51,-1);
            dp.push_back(tmp);
        }
        int ans=0;
        for(int i=0 ; i<=v[0] ; i++){
            ans=((ans%mod)+(rec(1,(i),dp,v,n)%mod))%mod;
        }
        return ans;
    }
};
