class Solution {
public:
    const int mod = 1e9+7;
    long long dp[2001][51][51];
    long long solve(vector<int>& nums, int idx, int x, int y) {
        if (idx >= nums.size()) {
            return 1; 
        }
        if (dp[idx][x][y] != -1){
            return dp[idx][x][y]; 
        }

        long long ans = 0;
        for (int i = x; i <= nums[idx]; i++) {
            for (int j = y; j >= 0; j--) {
                if (i + j == nums[idx]) {
                    ans = (ans + solve(nums, idx + 1, i, j)) % mod;
                }
            }
        }
        dp[idx][x][y] = ans; 
        return ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp, -1, sizeof(dp));
        return solve(nums, 0, 0, nums[0]);
    }

};
