class Solution {
public:
    int countOfPairs(vector<int>& nums) {

        int n = nums.size() ;


        vector<vector<vector<int>>> dp(n , vector<vector<int>>(51 , vector<int>(51 , -1))) ;
        int mod = 1e9 + 7 ;
        return numWays(0 , 0 , nums[0] , nums, dp, mod) ;

    }

private:
    int numWays(int index , int prevValueA , int prevValueB , vector<int>& nums, vector<vector<vector<int>>> &dp, int &mod ){

        if(index >= nums.size()) return 1 ;

        if(dp[index][prevValueA][prevValueB] != -1) return dp[index][prevValueA][prevValueB] ;


        long long int ways =0 ;
        for(int i = prevValueA ; i <= nums[index] ; i++){
            if(nums[index] - i <= prevValueB){
                // cout << index << i << " "  << nums[index] - i << endl ;
                ways += (0LL + numWays(index + 1 , i , nums[index] - i , nums, dp, mod)) % mod ;
            }
        }

        return dp[index][prevValueA][prevValueB] = ways % mod;
    }
};
