#define lint long long int
class Solution {
public:
    vector<vector<vector<lint>>> memo;
    lint MOD = 1e9+7;
    lint helper(int idx, int d, int b, vector<int>& a) {
        if(idx>=a.size()) return 1;
        if(memo[idx][d][b] != -1) return memo[idx][d][b];
        lint res = 0;
        for(int i=d;i<=a[idx];i++) {
            for(int j=b;j>=0;j--) { 
                if(i+j == a[idx]) {
                    res += helper(idx+1, i, j, a);
                    res =  res%MOD;
                }
            }
        }
        return memo[idx][d][b] = res;
    }

    int countOfPairs(vector<int>& a) {
        int n = a.size();
        memo = vector<vector<vector<lint>>>(n, vector<vector<lint>>(51, vector<lint>(51, -1)));
        return helper(0, 0, a[0], a);
    }
};
