class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        /*
        0 -> 1
        1 -> 1
        2 -> 1

        (0 -> >=0) (3 -> <=3)

        (1 -> >=1) (2 -> <=2)

        */
        int n=nums.size(),mod=1e9+7;
        int maxi=*max_element(nums.begin(),nums.end());
        vector<vector<int>> dpInc(n,vector<int>(maxi+1));
        for(int i=0;i<=nums.back();i++)
            dpInc[n-1][i]=1;

        for(int i=n-2;i>=0;i--){
            int curNum=nums[i];
            int nextNum=nums[i+1];
            for(int x1=0;x1<=curNum;x1++){
                int x2=curNum-x1;
                // >=x1 and <=x2
                for(int y1=0;y1<=nextNum;y1++){
                    int y2=nextNum-y1;
                    if(y1>=x1 and y2<=x2){
                        //dp state update
                        dpInc[i][x1]=(dpInc[i][x1]+dpInc[i+1][y1])%mod;
                    }
                }
            }
        }

        int totalCnt=0;
        for(int i=0;i<=maxi;i++)
            totalCnt=(totalCnt+dpInc[0][i])%mod;
        return totalCnt;
    }
};
