class Solution {
public:
    const int MOD = 1e9 + 7;
    int rec(const std::vector<int>& nums, int k, int prev, int prev1,vector<vector<vector<int>>>& dp) {
        if (k == nums.size()) return 1;
        if (dp[k][prev][prev1] != -1) return dp[k][prev][prev1];
        int cnt = 0;
        for (int i = prev; i <= nums[k]; i++) {
            int a = nums[k] - i;
            if (a <= prev1) {
                cnt = (cnt + rec(nums, k + 1, i, a, dp)) % MOD;
            }
        }
        dp[k][prev][prev1] = cnt;
        return cnt;
    }

    int countOfPairs(std::vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(51,vector<int>(51, -1)));
        return rec(nums, 0, 0, 50, dp);
    }
};
