class Solution {
public:
    const int MOD = 1e9 + 7;

    int solve(vector<vector<vector<int>>>& dp, vector<int>& nums, int index, int ndprev, int niprev) {
        if (index == nums.size()) {
            return 1;
        }

        if (dp[index][ndprev][niprev] != -1) {
            return dp[index][ndprev][niprev];
        }

        int curr = nums[index];
        int ans = 0;

        for (int i = 0; i <= curr; i++) {
            if (i >= ndprev && curr - i <= niprev) {
                ans = (ans + solve(dp, nums, index + 1, i, curr - i)) % MOD;
            }
        }

        return dp[index][ndprev][niprev] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(dp, nums, 0, 0, 50);
    }
};
