class Solution {
public:
    int mod = 1e9+7;

    int solve(int i, int n, vector<int>& nums, int prev1, int prev2, vector<vector<vector<int>>>& dp) {
        if(i == n) return 1;

        if(dp[i][prev1][prev2] != -1) return dp[i][prev1][prev2];

        int ans = 0;
        for(int j=prev1; j<=nums[i]; ++j) {
            int arr1 = j;
            int arr2 = nums[i]-arr1;

            if(arr1 >= prev1 && arr2 <= prev2) {
                ans = (ans  + solve(i+1, n, nums, arr1, arr2, dp) % mod) % mod;
            }
        }

        return dp[i][prev1][prev2] = ans % mod;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(52, vector<int>(52, -1)));
        return solve(0, n, nums, 0, 51, dp) ;
    }
};
