package main

import "fmt"

const MOD = 1000000007

func findPairs(nums []int, idx, prev1, prev2 int, dp [][][]int) int {

	if idx >= len(nums) {

		return 1

	}

	if dp[idx][prev1][prev2] != -1 {

		return dp[idx][prev1][prev2]

	}

	ans := 0

	for i := 0; i <= nums[idx]; i++ {

		arr1Val := i

		arr2Val := nums[idx] - i

		if arr1Val >= prev1 && arr2Val <= prev2 {

			ans = (ans + findPairs(nums, idx+1, arr1Val, arr2Val, dp)) % MOD

		}

	}

	dp[idx][prev1][prev2] = ans

	return ans

}

func countOfPairs(nums []int) int {

	n := len(nums)

	dp := make([][][]int, n)

	for i := range dp {

		dp[i] = make([][]int, 51)

		for j := range dp[i] {

			dp[i][j] = make([]int, 51)

			for k := range dp[i][j] {

				dp[i][j][k] = -1

			}

		}

	}

	return findPairs(nums, 0, 0, 50, dp)

}
