class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(), mod = 1e9 + 7;
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        auto rec = [&](auto&& rec, int idx, int val1, int val2) -> long long {
            if(idx >= n) return 1ll;
            if(dp[idx][val1][val2] != -1) return dp[idx][val1][val2];
            long long ans = 0;
            for(int i=0;i<=nums[idx];i++){
                int x = i, y = nums[idx] - i;
                if(x >= val1 && val2 >= y)
                    ans = (ans + rec(rec, idx+1, x, y)) % mod;
            }
            return dp[idx][val1][val2] = ans;
        };
        return rec(rec, 0, 0, 50);
    }
};
