class Solution {
public:
#define ll long long
    int countOfPairs(vector<int>& nums) {

        int n = nums.size();
        ll m = 1000000007;
        vector<vector<ll> > dp(n+1, vector<ll>(52,0));
        for(int i=0;i<n;++i){
            int curr = nums[i];
            if(!i){
                for(int j=0;j<=curr;++j){
                    dp[i][j]=1;
                }
            }
            else{

                for(int curr_ele =0;curr_ele<=curr;++curr_ele){
                    ll sum =0;
                    for(int prev_ele =0;prev_ele<=min(curr_ele,nums[i-1]);++prev_ele){
                        if(nums[i-1] - prev_ele < curr-curr_ele){
                            continue;
                        }
                        //cout<<prev_ele<<' '<<curr_ele<<' '<<i<<endl;
                        sum+=dp[i-1][prev_ele];
                        sum%=m;
                    }
                    dp[i][curr_ele]=sum;
                }
            }
        }
        //  [{0,0}], [{1,1},{1,2}], [{0,0}, {0,1},{0,2}]

        // for(int i=0;i<=3;++i){
        //     for(int j=0;j<=3;++j){
        //         cout<<dp[i][j]<<' ';
        //     }
        //     cout<<endl;
        // }

        ll ans =0;
        for(int i=0;i<=nums[n-1];++i){
            ans+=dp[n-1][i];
            ans%=m;
        }
        return ans;

    }
};
