MOD = 10**9 + 7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)

        #a1 is n0n-increasing, a2 is non-decreasing
        @cache
        def dfs(i, a1):
            if i == n:
                return 1
            #a2 最小取 nums[i-1]-a1， 最大取nums[i]-> a1最大取nums[i]-nums[i-1]+a1, 最小取0
            #a1 最小取 0， 最大取a1 
            ans = 0
            for j in range(0, min(a1, nums[i]-nums[i-1]+a1)+1):
                ans += dfs(i+1, j)%MOD
            return ans

        m = 0
        for k in range(nums[0]+1):
            m += dfs(1, k)%MOD
        return m%MOD
