class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int mod = pow(10, 9)+7;
        int max_elem = 0;
        for(int i=0; i<nums.size(); i++)
        max_elem = max(max_elem, nums[i]);

        vector<int> dp(max_elem+1, 0);
        for(int i=0; i<=nums[0]; i++)
        dp[i] = 1;

        for(int i=1; i<nums.size(); i++)
        {
            vector<int> temp(max_elem+1, 0);
            for(int j=0; j<=nums[i]; j++)
            {
                for(int k=0; k<=j; k++)
                {
                    int prev_val = nums[i-1]-k;
                    int curr_val = nums[i]-j;
                    if(prev_val >= curr_val)
                    temp[j] = (temp[j] + dp[k])%mod;
                }
            }
            dp = temp;
        }

        int ans = 0;
        for(auto it : dp)
        {
            ans = (ans + it)%mod;
        }
        return ans;
    }
};
