#define ll long long int
class Solution {
public:

    int helper(int index, int low, int high,vector<int>& values,  vector<vector<vector<long long >>> &dp){
        if (index >= values.size()) return 1;
        if (dp[index][low][high] != -1) return dp[index][low][high];

        ll result = 0;
        for (int i = low; i <= values[index]; i++) {
            for (int j = 0; j <= high; j++) {
                if (i + j == values[index]) {
                    result = (result + helper(index + 1, i, j, values , dp)) % 1000000007;
                }
            }
        }
        return dp[index][low][high] = result;
    }  
    int countOfPairs(vector<int>& values) {

        vector<vector<vector<ll>>> dp(values.size(), vector<vector<ll>>(51, vector<ll>(51, -1)));
        return helper(0, 0, values[0],values, dp);  
    }

};
