#include <vector>
#include <cstring>
using namespace std;

class Solution {
public:
    int memo[2001][51][51];
    const int MOD = 1e9 + 7;

    int countPairs(int idx, int lastA, int lastB, vector<int>& nums) {
        if (idx == nums.size()) return 1;
        if (memo[idx][lastA][lastB] != -1) return memo[idx][lastA][lastB];

        int result = 0;
        for (int a = lastA; a <= nums[idx]; ++a) {
            int b = nums[idx] - a;
            if (b <= lastB) {
                result = (result + countPairs(idx + 1, a, b, nums)) % MOD;
            }
        }

        return memo[idx][lastA][lastB] = result;
    }

    int countOfPairs(vector<int>& nums) {
        memset(memo, -1, sizeof(memo));
        return countPairs(0, 0, 50, nums);
    }
};
