class Solution {
public:
    int mod = 1e9+7;
    int solve(int idx, int last1, int last2, int n,vector<int> &nums,vector<vector<vector<int>>> &dp){
        if(idx == n) return 1;
        if(dp[idx][last1][last2] != -1) return dp[idx][last1][last2];

        int cnt = 0;
        for(int i = last1; i<= nums[idx]; ++i){
            int j  = nums[idx] - i;

            if(j <= last2){
                cnt = (cnt + solve(idx+1,i,j,n,nums,dp))%mod;
            }
        }
        return dp[idx][last1][last2] = cnt;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n+1, vector<vector<int>>(51,vector<int>(51,-1)));
        return solve(0,0,nums[0],n,nums,dp);
    }
};
