class Solution {
public:
    int MOD = 1000000007;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<long>>> dp(n, vector<vector<long>>(55, vector<long>(55, 0)));
        for(int i = 0; i <= nums[0]; ++i){
            dp[0][i][nums[0]-i] = 1;
        }
        for(int i = 1; i < n; ++i){
            int prev = nums[i-1];
            int cur = nums[i];
            for(int v1 = 0; v1 <= cur; ++v1){
                int v2 = cur - v1;
                for(int v11 = 0; v11 <= prev; ++v11){
                    int v22 = prev - v11;
                    if(v11 <= v1 && v22 >= v2){
                        dp[i][v1][v2] += dp[i-1][v11][v22];
                        dp[i][v1][v2] = dp[i][v1][v2] % MOD;
                    }
                }
            }
        }
        long res = 0;
        for(int i = 0; i <= nums[n-1]; ++i){
            res = (res + dp[n-1][i][nums[n-1] - i]) % MOD;
        }
        return res;
    }
};
