typedef long long ll;
#define repn(i, n, e) for(ll i = n; i < e; i++)
#define repr(j,n,e) for(int j = n; j >= e; j--)
#define rep(i, n, e) for(ll i = n; i <= e; i++)
#define re(i,n) for(const auto& i : n)
#define sor(a) sort(a.begin(), a.end())
#define as(a) a.begin(), a.end()
#define pb push_back
#define eb emplace_back
#define vp vector<pair<int, int>> 
#define MAP unordered_map<int, int>
#define F first
#define S second
#define r return
#define vec vector<int> 
#define mod 1000000007
class Solution {
public:
  int countOfPairs(vector<int>& values) {
      int size = values.size();
        vector<vector<vector<ll>>> memo(size, vector<vector<ll>>(51, vector<ll>(51, -1)));
        function<ll(int, int, int)> solve = [&](int index, int val1, int val2) -> ll {
            if (index >= size) return 1;
            if (memo[index][val1][val2] != -1){
                r memo[index][val1][val2];
            }

            ll result = 0;
            rep(i,val1,values[index]) {
                repr(j,val2,0) {
                    if (i + j == values[index]) {
                        result = (result + solve(index + 1, i, j)) % mod;
                    }
                }
            }
            r memo[index][val1][val2] = result;
        };
        r solve(0, 0, values[0]);
    }
};
