class Solution {
public:
    const int MOD = 1e9+7;
    int countOfPairs(vector<int>& nums) {
        // dp[i][x] = number of good pairs of nums[0..i] with arr1[i] == x
        // dp[i][x] = sum(dp[i-1][y < x]) with (nums[i-1] - y) >= (nums[i] - x)
        // O(nM^2)
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(51));
        for ( int x = 0; x <= nums[0]; ++x ) dp[0][x] = 1;
        for ( int i = 1; i < n; ++i ) {
            for ( int x = 0; x <= nums[i]; ++x ) {
                int sum = 0;
                for ( int y = 0; y <= x; ++y ) {
                    if ( (nums[i-1]-y) >= (nums[i]-x) ) sum = (sum + dp[i-1][y])%MOD;
                }
                dp[i][x] = sum;
            }
        }        
        int res = 0;
        for ( int x = 0; x <= nums[n-1]; ++x ) res = (res + dp[n-1][x])%MOD;
        return res;
    }
};
