class Solution {
public:
    int m = 1e9 + 7;
    int dp[2001][51];
    int solve(int currIdx,int prev1,vector <int> &nums){
        if(currIdx==nums.size()){
            return 1;
        }
        int prev2 = INT_MAX;
        if(currIdx!=0){
            prev2 = nums[currIdx-1] - prev1;
        } 

        if(dp[currIdx][prev1]!=-1){
            return dp[currIdx][prev1]%m;
        }  

        int ans = 0;

        for(int i=0;i<=nums[currIdx];i++){ // we shall give i to prev1
            if(i >= prev1 && nums[currIdx]-i <= prev2){
                ans = ((ans%m) + (solve(currIdx+1,i,nums)%m))%m;
            }
        }

        return dp[currIdx][prev1] = ans%m;
    }
    int countOfPairs(vector<int>& nums) {
        for(int i=0;i<2001;i++){
            for(int j=0;j<51;j++){
                dp[i][j] = -1;
            }
        }
        return solve(0,0,nums);
    }
};
