class Solution {
    int mod = (int) 1e9 + 7;

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        Integer dp[][][] = new Integer[n][101][101]; 
        return helper(0, -1, -1, nums, dp);
    }

    private int helper(int ind, int prev1, int prev2, int[] nums, Integer dp[][][]) {
        if (ind >= nums.length) {
            return 1;
        }

        if (dp[ind][prev1 + 1][prev2 + 1] != null) {
            return dp[ind][prev1 + 1][prev2 + 1];
        }

        int curr = nums[ind];
        int ans = 0;

        for (int i = 0; i <= curr; i++) {
            int curr1 = i;
            int curr2 = curr - i;

            if ((prev1 == -1 && prev2 == -1) || (curr1 >= prev1 && prev2 >= curr2)) {
                ans = (ans + helper(ind + 1, curr1, curr2, nums, dp)) % mod;
            }
        }

        return dp[ind][prev1 + 1][prev2 + 1] = ans;
    }
}
