class Solution {
public:
    long long compute(int idx, int prev1, int prev2, vector<int>& nums, vector<vector<vector<long long>>>& memo) {
    if (idx >= nums.size()) return 1;
    long long result = 0;
    long long mod_value = 1e9 + 7;

    if (memo[idx][prev1][prev2] != -1) return memo[idx][prev1][prev2];

    for (int x = prev1; x <= nums[idx]; x++) {
        for (int y = prev2; y >= 0; y--) {
            if (x + y == nums[idx]) {
                result += compute(idx + 1, x, y, nums, memo);
                result %= mod_value;
            }
        }
    }
    return memo[idx][prev1][prev2] = result;
}

int countOfPairs(vector<int>& nums) {
    int n = nums.size();
    vector<vector<vector<long long>>> memo(n, vector<vector<long long>>(51, vector<long long>(51, -1)));
    return compute(0, 0, nums[0], nums, memo);
}
};
