 class Solution {
    private static final int MOD = 1_000_000_007;
    private int[][][] dp;

    public Solution() {
        dp = new int[0][][];
    }

    private int findPairs(int[] nums, int idx, int prev1, int prev2) {
        if (idx >= nums.length) {
            return 1;
        }

        if (dp[idx][prev1][prev2] != -1) {
            return dp[idx][prev1][prev2];
        }

        int ans = 0;
        for (int i = 0; i <= nums[idx]; i++) {
            int arr1Val = i;
            int arr2Val = nums[idx] - i;
            if (arr1Val >= prev1 && arr2Val <= prev2) {
                ans = (ans + findPairs(nums, idx + 1, arr1Val, arr2Val)) % MOD;
            }
        }

        dp[idx][prev1][prev2] = ans;
        return ans;
    }

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        dp = new int[n][51][51]; // nums[i] <= 50
        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= 50; j++) {
                for (int k = 0; k <= 50; k++) {
                    dp[i][j][k] = -1;
                }
            }
        }
        return findPairs(nums, 0, 0, 50);
    }
}
