class Solution {
public:
    long long dp[2001][51];
    int mod = 1e9 + 7;
    long long solve(vector<int>& nums, int i, int a = 0, int b = 50) {
        if (i == nums.size()) return 1;
        if (dp[i][a] != -1) return dp[i][a];

        int ans = 0;
        for (int j = a;j <= nums[i];j ++) {
            int k = nums[i] - j;
            if (k <= b && k >= 0)
                ans += solve(nums, i + 1, j, k);
                ans%= mod;
        }

        return dp[i][a] = ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve(nums, 0)%mod;
    }
};
