MOD = 10**9 + 7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n=len(nums)
        @lru_cache(None)
        def dp(i, va):
            if i>=n-1: return 1
            vb=nums[i]-va
            # va<=x<=nums[i+1]
            # vb>=nums[i+1]-x>=0
            res=0
            for x in range(max(va, nums[i+1]-vb), nums[i+1]+1): res += dp(i+1, x)
            return res%MOD
        return sum(dp(0, va) for va in range(0, nums[0]+1))%MOD
