const int M = 1e9 + 7, N = 2001, A = 1001;
typedef long long ll;
class Solution {
public:
    int n;
    ll dp[N][A], pre[N][A], ans = 0;
    int countOfPairs(vector<int>& a) {
        n = a.size();
        for (int i = 0; i <= a[0]; i++) {
            dp[0][i] = 1;
        }
        for (int i = 0; i < A; i++) {
            pre[0][i] = dp[0][i];
            if (i > 0) {
                pre[0][i] = (pre[0][i] + pre[0][i - 1]) % M;
            }
        }
        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= a[i]; j++) {
                int t = min(j, a[i - 1] - a[i] + j);
                if (t >= 0) {
                    dp[i][j] = pre[i - 1][t];
                }
            }
            for (int j = 0; j < A; j++) {
                pre[i][j] = dp[i][j];
                if (j > 0) {
                    pre[i][j] = (pre[i][j] + pre[i][j - 1]) % M;
                }
            }
        }
        for (int i = 0; i < A; i++) {
            ans = (ans + dp[n - 1][i]) % M;
        }
        return ans;
    }
};
