class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function countOfPairs($nums) {
        $mod = 1000000007;
        $ans = 0;
        $count = count($nums);
        $mem = [];
        for ($num = 0; $num < $nums[0] + 1; ++ $num) {
            $mem[0][$num] = 1;
        }
        for ($index = 1; $index < $count; ++ $index) {
            for ($num = 0; $num < $nums[$index] + 1; ++ $num) {
                $sum = 0;
                for ($l = 0; $l <= $num; ++ $l) {
                    if ($nums[$index - 1] - $l >= $nums[$index] - $num) {
                        $sum += $mem[$index - 1][$l];
                        $sum %= $mod;
                    }
                }
                $mem[$index][$num] = $sum;
            }
        }

        $ans = 0;
        foreach ($mem[$count - 1] as $num => $cnt) {
            $ans += $cnt;
            $ans %= $mod;
        }

        return $ans;
    }
}
