class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int mod = 1000000007;
        long[][] dp = new long[n][51];

        for (int j=0; j<=nums[0]; j++) {
            dp[0][j] = 1;
        }

        for (int i=1; i<n; i++) {
            for (int j=0; j<=nums[i]; j++) {
                for (int k=0; k<=j; k++) {
                    if (nums[i] - j <= nums[i-1] - k) {
                        dp[i][j] = (dp[i][j] + dp[i-1][k]) % mod;
                    }
                }
            }
        }

        long ans = 0;

        for (int i=0; i<=50; i++) {
            ans = (ans + dp[n-1][i]) % mod;
        }
        return (int)ans;
    }
}
