#include<bits/stdc++.h>
using namespace std;

#define ff              first
#define ss              second
#define pb              push_back
#define MOD             1000000007


typedef long long int lli;

class Solution {
public:
	lli Add(lli a, lli b){
		return ((a % MOD) + (b % MOD)) % MOD;
	}
	lli Dfs(int idx, int prev_number, vector<int>& nums, vector<vector<lli>>& dp){
		if(idx >= (int)nums.size()) return 1;
		if(dp[idx][prev_number] != -1) return dp[idx][prev_number];
		lli ret = 0;
		int other_prev_number = idx ? nums[idx - 1] - prev_number : INT_MAX;
		for(int i = prev_number; i <= nums[idx]; i++){
			int other_number = nums[idx] - i;

			if(other_number <= other_prev_number) ret = Add(ret, Dfs(idx + 1, i, nums, dp));

		}
		return dp[idx][prev_number] = ret;
	}
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<lli>> dp(n, vector<lli>(55, -1));

        return Dfs(0, 0, nums, dp);
    }
};

