const int mod = 1'000'000'007;
const int mod2 = 998244353;
const int N = 3e5, M = N;
const int N1 = 10000000;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll n = nums.size();
        vector<vl> dp(n+7,vl(1007,0)),pre(n+7,vl(1007,0));
        for(int i = 0 ;i<=nums[0];i++){
                dp[0][i] = 1;
                pre[0][i] = 1;
                if(i) pre[0][i] += pre[0][i-1];
        }
        for(int i=1;i<n;i++){
                for(int j=0;j<=nums[i];j++){
                        ll mx = min(j,j-nums[i]+nums[i-1]);
                        if(mx<0) dp[i][j] = 0;
                        else dp[i][j] = pre[i-1][mx];
                }
                for(int j=0;j<=1000;j++){
                        pre[i][j] = dp[i][j];
                        if(j) pre[i][j] = (pre[i][j] + pre[i][j-1])%mod;
                }
        }
        ll ans = 0;
        for(ll i = 0; i<=nums[n-1];i++) ans = (ans + dp[n-1][i])%mod;
        return (int)ans;
    }
};
