typedef long long ll;
class Solution {
public:
    ll mod = 1e9+7;
    ll solve(vector<vector<int>>& dp, int i, int last, vector<int>& nums) {
        if(dp[i][last]!=-1) {
            return dp[i][last];
        }
        int n = nums.size();
        if(i==n) {
            return dp[i][last] = 1;
        }
        int last1 = 0, last2 = INT_MAX;
        if(i) {
            last1 = last;
            last2 = nums[i-1] - last;
        }
        ll ans = 0;
        for(int curr1 = last1; curr1 <= nums[i]; curr1++) {
            int curr2 = nums[i] - curr1;
            if(last1<=curr1 && last2>=curr2) {
                ans = (ans + solve(dp, i+1, curr1, nums))%mod;
            }
        }
        return dp[i][last] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n+1, vector<int>(51, -1));
        return solve(dp, 0, 0, nums);
    }
};
