class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        constexpr int kMod = 1e9 + 7;
        auto modAdd = [](int a, int b) { return (a+b) % kMod; };
        constexpr int kNumMax = 50;
        vector<int> dp(kNumMax+1); // arr1 size count
        for (int i = 0; i <= nums[0]; ++i) {
            dp[i] += 1;
        }
        for (int i = 1; i < nums.size(); ++i) {
            vector<int> dp2(kNumMax+1);
            int num = nums[i];
            for (int arr1Num = 0; arr1Num <= num; ++arr1Num) {
                int arr2Num = num - arr1Num;
                for (int preArr1Num = 0; preArr1Num <= arr1Num; ++preArr1Num) {
                    int preArr2Num = nums[i-1] - preArr1Num;
                    if (preArr2Num < arr2Num) continue;
                    dp2[arr1Num] = modAdd(dp2[arr1Num], dp[preArr1Num]);
                }
            }
            swap(dp, dp2);
        }
        return accumulate(dp.begin(), dp.end(), 0, modAdd);
    }
};
