M = 10**9 + 7

class BIT:
    '''BIT of a[1..n], 1-based indexing'''

    def __init__(self, n, arr=None):
        if arr is not None: 
            self.n, self.c = len(arr), [0] + arr
            for i in range(1, self.n+1):
                if (j := i + BIT.lowbit(i)) <= self.n:
                    self.c[j] += self.c[i]
        else:
            self.n, self.c = n, [0] * (n + 1)

    def lowbit(x): return x & -x

    def add(self, p, v=1):
        '''1-based indexing'''
        while p <= self.n:
            # self.c[p] += v
            self.c[p] = (self.c[p] + v) % M
            p += BIT.lowbit(p)

    def prefix_sum(self, r):
        '''sum of a[1..r], 1-based indexing'''
        res = 0
        while r > 0:
            res = (res + self.c[r]) % M
            r -= BIT.lowbit(r)
        return res

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        b = BIT(nums[0] + 1, [1] * (nums[0]+1))
        for x in islice(nums, 1, None):
            prev = b.n - 1
            nxt = BIT(x + 1)
            for i in range(x + 1):
                j = x - i
                k = min(i, prev - j, prev)
                if k >= 0: nxt.add(i + 1, b.prefix_sum(k + 1))
            b = nxt
        return b.prefix_sum(b.n)
