class Solution {
public:
    long long dp[2001][51][51];
    long long mod = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp[0][0][50] = 1;
        for(int k=0;k<n;k++) {
            for(int i=0;i<=nums[k];i++) {
                int x = i, y = nums[k] - i;
                for(int a=0;a<=x;a++) {
                    int b;
                    if (k == 0) b = 50 - a;
                    else b = nums[k - 1] - a;
                    if (b < y) continue;
                    dp[k + 1][x][y] += dp[k][a][b];
                    dp[k + 1][x][y] %= mod;
                }
            }
        }
        long long ans = 0;
        for(int k=0;k<=50;k++) {
            for(int i=0;i<=50;i++) {
                ans += dp[n][k][i];
                ans %= mod;
            }
        }
        return ans;
    }
};
