class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7
        n = len(nums)
        V = max(nums)
        dp = [[0 for _ in range(V+1)] for _ in range(V+1)]
        for x in range(nums[0] + 1):
            dp[x][nums[0] - x] += 1

        for i in range(1, n):
            nd = [[0 for _ in range(51)] for _ in range(51)]
            for t in range(nums[i] + 1):
                a, b = t, nums[i] - t
                for x in range(V+1):
                    y = nums[i-1] - x
                    if a < x or b > y:
                        continue
                    nd[a][b] += dp[x][y]
                nd[a][b] %= mod
            dp = nd
        ans = 0
        for x in dp:
            ans += sum(x)
            ans %= mod
        return ans
