class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int md=1e9+7;

        int dp[n+1][51];memset(dp,0,sizeof(dp));
        int dp1[n+1][51];memset(dp1,0,sizeof(dp1));

        for(int i=0;i<=nums[0];i++)dp[0][i]=1;


        for(int i=1;i<n;i++){

            for(int j=nums[i];j>=0;j--){

                for(int k=nums[i-1];k>=j;k--){

                    if(nums[i-1]-k > nums[i]-j || nums[i-1]-k < 0)continue;
                    dp[i][nums[i]-j] = (dp[i][nums[i]-j]+dp[i-1][nums[i-1]-k])%md;
                }
                // cout<<dp[i][nums[i]-j]<<" ";
            }
            // cout<<endl;
        }
        int ans=0;
        // for(int i=0;i<n;i++){
        //     for(int j=0;j<=nums[i];j++)cout<<dp[i][j]<<" ";cout<<endl;
        // }
        for(int i=0;i<=nums[n-1];i++){
            ans=(ans+dp[n-1][i])%md;
        }
        return ans;
    }
};
