class Solution {
    long long mod = 1e9 + 7;
public:
    int countOfPairs(vector<int>& nums) {
        vector<long long> prev(1, 1);
        int prevN = -1;
        for(auto n: nums) {
            vector<long long> curr(n + 1);
            for(int i = 0; i <= n; i++) {
                for(int j = 0; j < prev.size() and j <= i; j++) {
                    if(prevN == -1 or prevN - j >= n - i)
                        curr[i] += prev[j];
                }
                curr[i] %= mod;
            }
            swap(curr, prev);
            prevN = n;
        }
        return accumulate(prev.begin(), prev.end(), 0LL) % mod;
    }
};
