class Solution {
    private final static int MOD = 1000000007;

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long[] dp = new long[51];
        for (int i = 0; i <= nums[0]; i++) {
            dp[i] = 1;
        }
        long[] ps = prefixSum(dp);
//        System.out.println(Arrays.toString(dp));
        for (int i = 1; i < n; i++) {
            long[] dpNext = new long[51];
            for (int j = 0; j <= nums[i]; j++) {
                int ub1 = j;
                int ub2 = nums[i - 1] - (nums[i] - j);
                dpNext[j] = ps[Math.max(0, Math.min(ub1, ub2) + 1)];
            }
            dp = dpNext;
            ps = prefixSum(dp);
//            System.out.println(Arrays.toString(dp));
        }
        return (int) ps[51];
    }

    private long[] prefixSum(long[] a) {
        long[] out = new long[a.length + 1];
        for (int i = 0; i < a.length; i++) {
            out[i + 1] = out[i] + a[i];
            out[i + 1] %= MOD;
        }
        return out;
    }
}
