#define mod 1000000007
class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n=a.size();
        vector <vector<int>> dp(n+1,vector<int>(51));
        int mn=1e9;
        for (int i=0;i<n;i++){
            if (!i){
                for (int j=0;j<=50;j++){
                    int f1=j,f2=a[i]-j;
                    if (f1>=0 && f2>=0){
                        dp[i][j]=1;
                    }
                }
            }
            else{
                for (int j=0;j<=50;j++){
                    int a2=j,b2=a[i]-j;
                    for (int k=0;k<=50;k++){
                        int a1=k,b1=a[i-1]-k;
                        if (a1>=0 && b1>=0 && a2>=0 && b2>=0 && a1<=a2 && b1>=b2){
                            dp[i][j]+=dp[i-1][k];
                            dp[i][j]%=mod;
                        }
                    }
                }
            }
        }
        int ans=0;
        for (int j=0;j<=50;j++){
            ans+=dp[n-1][j];
            ans%=mod;
        }
        return ans;
