class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int n = nums.size();
        const long long mod = 1'000'000'007LL;
        vector<vector<long long>> dp(51, vector<long long>(51));
        vector<vector<long long>> prev_dp(51, vector<long long>(51));
        for (int i = 0; i < dp.size() && i <= nums[0]; ++i) {
            dp[i][nums[0] - i] = 1;
        }
        for (int index = 1; index < n; ++index) {
            swap(dp, prev_dp);
            for (auto &row : prev_dp) {
                for (int j = row.size() - 2; j >= 0; --j) {
                    row[j] = (row[j] + row[j + 1]) % mod;
                }
            }
            for (auto &row : dp) {
                fill(row.begin(), row.end(), 0);
            }
            for (int i = 0, j = nums[index]; j >= 0; ++i, --j) {
                for (int ii = 0; ii <= i; ++ii) {
                    dp[i][j] = (dp[i][j] + prev_dp[ii][j]) % mod;
                }
            }
        }
        long long result = 0;
        for (const auto &row : dp) {
            for (long long col : row) {
                result = (result + col) % mod;
            }
        }
        return result;
    }
};
