const int MOD = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int arr1[51] = {};

        for (int i = 0; i <= nums[0]; i++) {
            arr1[i] = 1;
        }

        for (int i = 1; i < nums.size(); i++) {
            int next_arr1[51] = {};

            for (int j = 0; j < 51; j++) {
                int last_arr1 = j;
                int last_arr2 = nums[i - 1] - j;

                for (int k = last_arr1; k <= nums[i]; k++) {
                    if (last_arr2 >= nums[i] - k) {
                        next_arr1[k] = (next_arr1[k] + arr1[j]) % MOD;
                    }
                }
            }

            swap(next_arr1, arr1);
        }

        int sum = 0;
        for (int i = 0; i < 51; i++) {
            sum = (sum + arr1[i]) % MOD;
        }

        return sum;
    }
};
