func countOfPairs(nums []int) int {
    var MOD = 1000000007

    var dp = make([][]int, len(nums))
    for i := range dp {
        dp[i] = make([]int, 51)
    }

    for i := 0; i <= nums[0]; i++ {
        dp[0][i] = 1
    }

    for i := 1; i < len(nums); i++ {
        for k := 0; k <= nums[i]; k++ {
            for j := 0; j <= k; j++ {
                if nums[i-1] - j >= nums[i] - k {
                    dp[i][k] += dp[i-1][j]
                    dp[i][k] %= MOD
                }
            }
        }
    }

    var res = 0
    for i := 0; i <= nums[len(nums)-1]; i++ {
        res += dp[len(nums)-1][i]
        res %= MOD
    }

    return res
}
