typedef long long ll;

class Solution {
public:
    ll mod=(1e+9)+7;
    ll solve(int i,int prev,vector<int> &nums,vector<vector<ll>> &dp){
        if(i==nums.size()){
            return 1LL;
        }
        if(dp[i][prev]!=-1){
            return dp[i][prev];
        }
        ll ans=0;
        ll prev2=(i>0?nums[i-1]-prev:INT_MAX);
        for(int curr=prev;curr<=nums[i];curr++){
            if(nums[i]-curr<=prev2){
                (ans+=solve(i+1,curr,nums,dp))%=mod;
            }
        }
        return dp[i][prev]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<ll>> dp(n,vector<ll>(51,-1));
        return (int)solve(0,0,nums,dp);
    }
};
