const int mod = 1e9 + 7;

int dp[2010][51];

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();       

        function<int(int, int, int)> f = [&](int pos, int prev1, int prev2)
        {
              if(pos == n)
              {
                  return 1;
              }

              int &ans = dp[pos][prev1]; if(ans != -1) return ans;

              ans = 0;
              for(int i = 0; i <= nums[pos]; ++i)
              {
                  int num1 = i;
                  int num2 = nums[pos] - i;
                  if(num1 >= prev1 and num2 <= prev2)
                  {
                        ans += f(pos + 1, num1, num2);
                        ans %= mod;
                  }
              }

            return ans;
        };


        for(int i = 0; i < n; ++i)
        {
            for(int j = 0; j <= 50; ++j)
            {
                dp[i][j] = -1;
            }
        }


        int ans = 0;
        for(int i = 0; i <= nums[0]; ++i)
        {
              int num1 = i;
              int num2 = nums[0] - i;
              ans += f(1, num1, num2);
              ans %= mod;
          }


        return ans;
    }
};
