#define lln long long
class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        lln mod = 1e9+7;
        lln dp[n+1][52][52];
        memset(dp,0,sizeof dp);
        dp[0][0][0] = 1;
        for(int i=0;i<n;i++){

            if(i==0){
                for(int j=0;j<=a[i];j++){
                    dp[0][j][a[i]-j]++;
                }
            }
            else{
                for(int sp = 0;sp<=a[i];sp++){
                    for(int pv = 0;pv<=min(a[i-1],sp);pv++){
                        int ssp = a[i]-sp;
                        int spv = a[i-1]-pv;
                        if(ssp<=spv)dp[i][sp][ssp]=(dp[i][sp][ssp] + dp[i-1][pv][spv])%mod;
                    }
                }
            }

        }
        lln ans = 0;
        for(int i=0;i<=a[n-1];i++){
            ans = (ans+ dp[n-1][i][a[n-1]-i])%mod;
        }
        return ans;
    }
};
