class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7

        dp = defaultdict(int)
        dp[(-inf, inf)] = 1

        for num in nums:
            newdp = defaultdict(int)
            for a in range(num + 1):
                b = num - a
                for (dpa, dpb) in dp:
                    if dpa <= a and dpb >= b:
                        newdp[(a, b)] += dp[(dpa, dpb)]
                        newdp[(a, b)] %= mod
            dp = newdp

        res = 0
        for a, b in dp:
            res += dp[(a, b)]
            res %= mod

        return res
