class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int[][] dp = new int[n][1001];
        dp[0][0] = 1;
        for(int j = 1; j <= 1000; j++) {
            dp[0][j] = dp[0][j-1];
            if(j <= nums[0]) {
                dp[0][j]++;
            }
        }
        for(int i = 1; i < n; i++) {
            for(int j = 0; j <= 1000; j++) {
                if(j <= nums[i]) {
                    int k = nums[i]-j;
                    int maxpj = Math.min(j, nums[i-1]-k);
                    if(maxpj >= 0) {
                        dp[i][j] = dp[i-1][maxpj];
                    }
                }
                if(j > 0) {
                    dp[i][j] = (dp[i][j]+dp[i][j-1])%1000000007;
                }
            }
        }
        return dp[n-1][1000];
    }
}
