class Solution {
public:
    const int mod = 1e9 + 7;
    int n;
    vector<vector<int>> dp;
    int rec(int idx, int l1, vector<int> &nums) {
        if (idx == n) {
            return 1;
        }
        if (dp[idx][l1] != -1) {
            return dp[idx][l1];
        }
        int l2 = nums[idx];
        if (idx != 0) {
            l2 = nums[idx - 1] - l1;
        }
        int t1 = l1;
        int t2 = nums[idx] - l2;
        int t = max(t1, t2);
        int ans = 0;
        for (int i = t; i <= nums[idx]; i++) {
            ans = (0ll + ans + rec(idx + 1, i, nums)) % mod;
        }
        return dp[idx][l1] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        dp.assign(n, vector<int> (51, -1));
        int ans = rec(0, 0, nums);
        return ans;
    }
};
